import logo from './logo.svg';
import './App.css';
import {Data} from './Data.js';



function App() {
  return (
    <div className="box">
      <div className="list-ytb">
      {Data.map((item, index)=> (
        <Future
          key={index}
          img={item.img}
          avt={item.avt}
          titile={item.titile}
          des={item.des}
        >
        </Future>
      ))}
    </div>
    </div>
  );
}

function Future(props) {
  return (
    <div className="future">
      <div className="test">
        <img src= {props.img} alt="#"/>
        <div className="avatar">
          <img src={props.avt} alt="#" />
          <div className="autor">
            <h3>{props.titile}</h3>
            <p>{props.des}</p>
          </div>
        </div>
      </div>
    </div>

  );
}


export default App;
