import React, { useReducer, useState } from 'react';
import './style.css'

//Các bước để tạo nên useReducer
//1. Init state: 0
//2. Actions: Up (state + 1), Down (state - 1)
//3. reducer
//4. Dispatch


//1. Init state: 0
const state = 0;

//2. Actions: Up (state + 1), Down (state - 1)
const ACTION_DOWN = 'down'
const ACTION_UP = 'up'

//3. reducer
const reducer = (state, action) => {
    switch (action) {
        case ACTION_DOWN:
            return state - 1
        case ACTION_UP:
            return state + 1
        default:
            break;
    }
}


const CountDown = () => {
    const [count, dispatch]= useReducer(reducer, state)

    return (
        <div>
            <h1>{count}</h1>
            <button
                onClick={() => dispatch(ACTION_DOWN)}
            >
                Down
            </button>
            <button
                onClick={() => dispatch(ACTION_UP)}
            >
                Up
            </button>
        </div>
    );
};

export default CountDown;
