import React, { useState } from 'react';
import './style.css'

const CountDown = () => {
    const [count, setCount]= useState(0)

    return (
        <div>
            <h1>{count}</h1>
            <button
                onClick={setCount(count-1)}
            >
                Down
            </button>
            <button
                onClick={setCount(count+1)}
            >
                Up
            </button>
        </div>
    );
};

export default CountDown;
