import React from "react";

const Banner = () => {
  return (
    <section className="h-[700px] w-full mt-[130px]">
      <div
        style={{ backgroundImage: 'url("test.png")' }}
        className="bg-no-repeat opacity-70 bg-stone-700 h-full w-full flex"
      >
        <div className="text-white flex justify-center items-center flex-col font-bold w-full">
          <p className="text-[20px]">Food app</p>
          <h1 className="text-[40px] my-2 text-center">
            Why stay hungry when you can order form Bella Onajie
          </h1>
          <p className="text-[20px]">
            Download the bella onajie food app now on
          </p>
          <div className="mt-[20px]">
            <button className="bg-orange-600 border-orange-600 border-2 px-5 py-2 rounded-lg hover:bg-orange-300 transition duration-[0.4s]">
              PlayStore
            </button>
            <button className="border-white rounded-lg border-2 px-5 py-2 ml-5 hover:bg-orange-300 transition duration-[0.4s]">
              AppStore
            </button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Banner;
