import React from 'react';
import styled from "styled-components" 


const StyledCard = styled.div `
    position: relative;
    width: 400px;
    height: 400px;
`;

const StyledImage = styled.div ` 
    display: inline-block;
    width: 400px;
    height: 400px;
    border-radius:8px
`;


const StyledImg = styled.img `
    width: 100%;
    height: 100%;
    border-radius: inherit;
    object-fit: cover;
`;

const StyledContent = styled.div `
    position: absolute;
    z-index: 10;
    background-color: red;
    bottom: -60px;
    left: 50%;
    transform: translateX(-50%);
    padding: 20px;
    width: 360px;
    height: 125px;
    border-radius: 20px;
`;

const StyledTop = styled.div `
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 10px;
`;

const ContentImg = styled.img `
    width: 30px;
    height: 30px;
    border-radius: 50%;
`;

const Name = styled.span `
    color: #333333;
    font-size: 16px;

`

const StyledBottom = styled.div `
    display: flex;
    justify-content: space-between;
    align-items: center;
`;

const StyledText = styled.span `
    background: linear-gradient(86.88deg, #7D6AFF 1.38%, #FFB86C 64.35%, #FC2872 119.91%);
    color: transparent;
    font-weight: bold;
    font-size: 18px; 
    -webkit-background-clip: text;
    background-clip: text;
`

const Card = () => {
    return (
        <StyledCard>
            <StyledImage>
                <StyledImg src="https://cdn.dribbble.com/userupload/11087035/file/original-d18e5383bf37c6baaf074a8b3c5b5447.png?resize=1200x900" alt="#" />
            </StyledImage>
            <StyledContent>
                <StyledTop>
                    <div>
                        <ContentImg src="https://cdn.dribbble.com/userupload/11087035/file/original-d18e5383bf37c6baaf074a8b3c5b5447.png?resize=1200x900" alt="#" />
                        <Name>@zndrson</Name>
                    </div>
                    <div>
                        <span>256</span>
                    </div>
                </StyledTop>
                <StyledBottom>
                    <h3>Cosmic Perspective</h3>
                    <StyledText>12,000 PSL</StyledText>
                </StyledBottom>
            </StyledContent>
        </StyledCard>
    );
};

export default Card;
